	(106, 'joe@kamidev.org', 0, 8, '{"link": "http://viablecities.com/foi-projekt/sharing-behaviour/", "dates": {"end": "2020-12-31", "start": "2019-01-01"}, "budget": {"funded": 1800000, "total_cost": 3800000}, "website": "https://www.sharingcities.se/investigating", "keywords": ["beteenden", "livsstil", "attityd", "värdering", "energi", "delningstjänst", "delningsekonomi", "hållbar utveckling"], "locations": ["Lund"], "project_id": "2018-04649", "description": "Projektet ingår i programmet Sharing Cities Sweden. Många initiativ inom delningsekonomi som lanserats runt om i världen har inte lyckats som förväntat. Bristande kunskap om de bakomliggande orsakerna till (icke) adoption, skalning och mainstreaming bland användarna är huvudorsakerna. Projektet syftar till att utveckla beteendeekonomiska experiment som testar, granskar och genererar policyrekommendationer för att främja satsningar inom delningsekonomi i städer.", "project_type": "Innovationsprojekt", "ratio_funded": 0.473684210526, "project_title": "Sharing behaviour", "focus_mobility": [], "focus_planning": [], "focus_lifestyle": [], "project_manager": "Luis Mundaca", "other_financiers": ["Uppgift saknas"], "other_recipients": [], "focus_infrastructure": [], "project_organization": "Lund university", "project_manager_email": "luis.mundaca@iiiee.lu.se"}', '2019-10-25 17:42:20', '2019-10-25 17:42:20', '2018-04649'),
